mapper.select:
SELECT ploID, balance, phoneNumber, fullName, password, email, status, identify, parkingName, description, address, latitude, longtitude, parkingStatusID, slot, currentSlot, role, length, width, waitingTime, cancelBookingTime, contractLink, registerContract, browseContract, contractDuration, star FROM plo WHERE parkingStatusID IN
#{status}
LIMIT #{pageSize} OFFSET #{pageNum};
