mapper.select:
SELECT R.reservationID,R.customerID,R.ploID,R.statusID,LP.licensePlate,R.startTime,R.endTime,R.price,R.checkOut,RM.methodName,R.checkIn
FROM Reservation AS R
JOIN LicensePlate AS LP ON R.licensePlateID = LP.licensePlateID
JOIN ReservationMethod RM ON R.methodID = RM.methodID
WHERE R.ploID = #{ploID}
AND R.statusID = #{status}
AND LP.licensePlate = #{licensePlate};

SELECT SUM(price) AS total_price
FROM Reservation
WHERE DATE(startTime) = CURDATE() AND ploID = #{ploID};

SELECT IFNULL(SUM(price), 0) AS totalPrice
FROM Reservation
WHERE startTime >= #{startTime} AND startTime <= CONCAT(#{startTime2th}, ' 23:59:59') AND ploID = #{ploID}

SELECT reservationID, customerID, ploID, statusID, licensePlateID, startTime, endTime, price, checkOut, checkIn, methodID, isRating FROM reservation WHERE statusID IN
#{st}
AND customerID = #{customerID};

SELECT R.reservationID,R.customerID,R.ploID,R.statusID,R.licensePlateID,R.startTime,R.endTime,R.price,R.checkOut,R.checkIn, R.methodID, R.isRating
FROM Reservation AS R
JOIN LicensePlate AS LP ON R.licensePlateID = LP.licensePlateID
JOIN ReservationMethod RM ON R.methodID = RM.methodID
WHERE R.ploID = #{ploId}
AND R.statusID
NOT IN

        #{st}

AND LP.licensePlate = #{licensePlate};

mapper.update:
UPDATE reservation
SET isRating = #{isRating}
WHERE reservationID = #{reservationID}

UPDATE reservation
SET statusID = #{statusID},
    isRating = #{isRating}
WHERE reservationID = #{reservationID}
