mapper.update:
UPDATE Reservation
SET statusID = #{statusID}
WHERE reservationID = #{reservationID};

UPDATE Reservation
SET checkOut = #{checkOut}
WHERE reservationID = #{reservationID};

UPDATE Reservation
SET checkIn = #{checkIn}
WHERE reservationID = #{reservationID};

mapper.select:
SELECT methodID, methodName,startTime,endTime
FROM ReservationMethod
WHERE TIMESTAMP #{currentTime} BETWEEN startTime AND endTime;
